/**
 * Image utility functions for handling image URLs
 */

/**
 * Get the full image URL for a given image path
 * Handles both relative paths (like /imgs/phone.jpg) and full URLs
 */
export const getImageUrl = (imagePath: string): string => {
  if (!imagePath) {
    return '';
  }
  
  // If it's already a full URL, return as is
  if (imagePath.startsWith('http')) {
    return imagePath;
  }
  
  // If it's a relative path, prepend the backend URL
  const backendUrl = 'http://localhost:9090';
  return `${backendUrl}${imagePath}`;
};

/**
 * Get a fallback image URL for when images fail to load
 */
export const getFallbackImage = (): string => {
  return 'data:image/png;base64,...';
};
